mapper.select:
SELECT 

FROM tl_account a

WHERE a.id = #{id}

SELECT 

FROM tl_account a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tl_account a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tl_account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
