mapper.select:
SELECT 

FROM tl_group a

WHERE a.id = #{id}

SELECT 

FROM tl_group a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.url = #{url}


		AND a.is_channel = #{isChannel}




		ORDER BY ${page.orderBy}


	order by upcate_date desc

SELECT 

FROM tl_group a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}

mapper.update:
UPDATE tl_group SET 	
	offset_=#{offset},
	update_by = #{updateBy.id},
	upcate_date = #{upcateDate}
WHERE id = #{id}

UPDATE tl_group SET 	
	update_num=update_num+1,
	update_by = #{updateBy.id},
	upcate_date = #{upcateDate}
WHERE id = #{id}

UPDATE tl_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
