mapper.select:
SELECT 

FROM tl_group a

WHERE a.id = #{id}

SELECT 

FROM tl_group a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.url = #{url}


		AND a.is_channel = #{isChannel}




		ORDER BY ${page.orderBy}

SELECT 

FROM tl_group a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}

mapper.update:
UPDATE tl_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
