mapper.select:
SELECT 

FROM tl_job a

WHERE a.id = #{id}

SELECT 

FROM tl_job a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.group_id = #{groupId}


		AND a.from_group_id = #{fromGroupId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tl_job a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tl_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
